SELECT KDDATI4, KDDATI3, KDDATI2, KDDATI1, NMDATI4, UPDSTAMP FROM
(SELECT KDDATI4, A.KDDATI3, A.KDDATI2, A.KDDATI1, NMDATI4, A.UPDSTAMP,
ROW_NUMBER() OVER (PARTITION BY KDDATI4, A.KDDATI3, A.KDDATI2, A.KDDATI1 ORDER BY A.UPDSTAMP DESC) AS row_num
FROM MIG_ACB.DATI4_TBL A INNER JOIN REF_DATI3 B ON 
A.KDDATI3 = B.KDDATI3 AND
A.KDDATI2 = B.KDDATI2 AND
A.KDDATI1 = B.KDDATI1 
)
WHERE ROW_NUM =1;

SELECT KDCABANG, KDPRODUK, KDVOUCHER, KDDANA, NMDANA, STATUS, UPDSTAMP, 'migrasi NCB' 
    FROM ( SELECT KDCABANG, KDPRODUK, KDVOUCHER, KDDANA, NMDANA, STATUS, UPDSTAMP, 
           ROW_NUMBER() OVER (PARTITION BY KDCABANG, KDPRODUK, KDVOUCHER, KDDANA ORDER BY UPDSTAMP DESC) AS row_num
           FROM MIG_ACB.DANA_TBL)
    WHERE row_num =1;

